//javapractice
import java.util.Scanner;
//do__while loop
//reverse number

public class p12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		System.out.println("Enter a number:");
		int no=sc.nextInt();
		int i=1;
		do {
			System.out.println("hiii");
			i++;
		}while(i<=no);
	}

}
